import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { Link } from "react-router-dom";

export default function PlacePage() {
  const { id } = useParams();
  const [place, setPlace] = useState(null);

  useEffect(() => {
    if (!id) {
      return;
    }
    axios.get(`/places/${id}`).then((response) => {
      setPlace(response.data);
    });
  }, [id]);
  if (!place) {
    return "";
  }

  return (
    <>
      <div className="mt-4 pt-4 bg-gray-100 -mx-8 px-8 py-8">
        <h1 className="text-3xl">{place.title}</h1>
        <a
          className="block font-semibold my-2 underline"
          target="_blank"
          href={"https://maps.google.com/?q=" + place.address}
        >
          {place.address}
        </a>
        <div className="grid gap-2 grid-cols-[2fr_1fr]">
          <div>
            {place.photos?.[0] && (
              <img className="aspect-square object-cover" src={"http://localhost:4000/uploads/" + place.photos[0]} />
            )}
          </div>
          <div className="grid">
            {place.photos?.[0] && (
              <img className="aspect-square object-cover" src={"http://localhost:4000/uploads/" + place.photos[0]} />
            )}
            {place.photos?.[1] && (
              <img className="aspect-square object-cover" src={"http://localhost:4000/uploads/" + place.photos[1]} />
            )}
          </div>
        </div>
      </div>
    </>
  );
}
