import { useEffect, useState } from "react";
import PhotosUploader from "./photosUploaded";
import Perks from "./perks";
import axios from "axios";
import AccountNavigation from "./accountNavigation";
import { Navigate, useParams } from "react-router-dom";

export default function PlacesForm() {
  const { id } = useParams();
  const [title, setTitle] = useState("");
  const [address, setAddress] = useState("");
  const [addedPhotos, setAddedPhotos] = useState([]);
  const [description, setDescription] = useState("");
  const [perks, setPerks] = useState([]);
  const [extrainfo, setExtrainfo] = useState("");
  const [checkIn, setCheckIn] = useState("");
  const [checkOut, setCheckOut] = useState("");
  const [maxGuest, setMaxGuest] = useState(1);
  const [price,setPrice]=useState(100);
  const [redirect, setRedirect] = useState(false);

  useEffect(() => {
    if (!id) {
      return;
    }
    axios.get("/places/" + id).then((response) => {
      const { data } = response;
      setTitle(data.title);
      setAddress(data.address);
      setAddedPhotos(data.photos);
      setDescription(data.description);
      setPerks(data.perks);
      setExtrainfo(data.extrainfo);
      setCheckIn(data.checkIn);
      setCheckOut(data.checkOut);
      setMaxGuest(data.maxGuest);
      setPrice
    });
  }, [id]);

  function inputHeader(text) {
    return <h2 className="text-2xl mt-4">{text}</h2>;
  }
  function inputDescription(text) {
    return <p className="text-gray-500 text-sm">{text}</p>;
  }

  function input(header, description) {
    return (
      <>
        {inputHeader(header)}
        {inputDescription(description)}
      </>
    );
  }

  async function savePlace(ev) {
    ev.preventDefault();
    const placeData = {
      title,
      address,
      addedPhotos,
      description,
      perks,
      extrainfo,
      checkIn,
      checkOut,
      maxGuest,
      price
    };

    if (id) {
      // id there
      await axios.put("/places", {
        id,
        ...placeData,
      });
      setRedirect(true);
    } else {
      //new place
      await axios.post("/places", placeData);
      setRedirect(true);
    }
  }

  if (redirect) {
    return <Navigate to={"/account/places"} />;
  }

  return (
    <>
      <div>
        <AccountNavigation />
        <form onSubmit={savePlace}>
          {input("title", "Enter the title for your places")}
          <input
            type="text"
            placeholder="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
          {input("address", "Enter your address")}
          <input
            type="text"
            placeholder="address"
            value={address}
            onChange={(e) => setAddress(e.target.value)}
          />
          {input("Photos", "More=better")}
          <PhotosUploader addedPhotos={addedPhotos} onChange={setAddedPhotos} />
          {input("Description", "Enter the Description for your places")}
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
          {input("Perks", "Select all the Perks")}
          <Perks selected={perks} onChange={setPerks} />
          {input("Extra Info", "Please provide the extra info")}
          <textarea
            value={extrainfo}
            onChange={(e) => setExtrainfo(e.target.value)}
          />
          {input(
            "Check-In & Check-Out & Max-Guest",
            "Add Check-In Check-Out and Max-Guest"
          )}
          <div className="grid grid-cols-2 md:grid-cols-4 gap-2">
            <div>
              <h3 className="mt-2 -mb-1 ">Check in Time</h3>
              <input
                type="text"
                placeholder="16:00"
                value={checkIn}
                onChange={(e) => setCheckIn(e.target.value)}
              />
            </div>
            <div>
              <h3 className="mt-2 -mb-1 ">check out time</h3>
              <input
                type="text"
                placeholder="11"
                value={checkOut}
                onChange={(e) => setCheckOut(e.target.value)}
              />
            </div>
            <div>
              <h3 className="mt-2 -mb-1 "> Max Number of guest</h3>
              <input
                type="number"
                placeholder=""
                value={maxGuest}
                onChange={(e) => setMaxGuest(e.target.value)}
              />
            </div>
            <div>
              <h3 className="mt-2 -mb-1 ">Price per Night</h3>
              <input
                type="number"
                placeholder=""
                value={price}
                onChange={(e) => setPrice(e.target.value)}
              />
            </div>
          </div>
          <button className="primary mt-4">Save</button>
        </form>
      </div>
    </>
  );
}
