import { useState } from "react";
import { Link, useParams } from "react-router-dom";
import Perks from "./perks";
import axios from "axios";

export default function PlacesPage() {
  const { action } = useParams();
  const [title, setTitle] = useState("");
  const [address, setAddress] = useState("");
  const [addedPhotos, setAddedPhotos] = useState([]);
  const [photolink, setPhotolink] = useState("");
  const [description, setDescription] = useState("");
  const [perks, setPerks] = useState([]);
  const [extrainfo, setExtrainfo] = useState("");
  const [checkIn, setCheckIn] = useState("");
  const [checkOut, setCheckOut] = useState("");
  const [maxGuest, setMaxGuest] = useState("");

  function inputHeader(text) {
    return <h2 className="text-2xl mt-4">{text}</h2>;
  }
  function inputDescription(text) {
    return <p className="text-gray-500 text-sm">{text}</p>;
  }

  function input(header, description) {
    return (
      <>
        {inputHeader(header)}
        {inputDescription(description)}
      </>
    );
  }

  async function addPhotosbyLink(ev) {
    ev.preventDefault();
    const { data: filename } = await axios.post("/upload-by-link", {
      link: photolink,
    });
    console.log(filename);
    setAddedPhotos((prev) => {
      return [...prev, filename];
    });
    setPhotolink("");
  }

  return (
    <div>
      {action !== "new" && (
        <div className="text-center">
          <Link
            className="inline-flex gap-1 bg-primary text-white py-2 px-6 rounded-full"
            to={"/account/places/new"}
          >
            <svg
              xmlns="http://www.w3.org/2000/svg"
              fill="none"
              viewBox="0 0 24 24"
              strokeWidth={1.5}
              stroke="currentColor"
              className="w-6 h-6"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                d="M12 9v6m3-3H9m12 0a9 9 0 1 1-18 0 9 9 0 0 1 18 0Z"
              />
            </svg>
            Add A new Places
          </Link>
        </div>
      )}
      {action === "new" && (
        <div>
          <form>
            {input("title", "Enter the title for your places")}
            <input
              type="text"
              placeholder="title"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
            />
            {input("address", "Enter your address")}
            <input
              type="text"
              placeholder="address"
              value={address}
              onChange={(e) => setAddress(e.target.value)}
            />
            {input("Photos", "More=better")}
            <div className="flex gap-2">
              <input
                type="text"
                placeholder="add using a link ....jpg"
                value={photolink}
                onChange={(e) => setPhotolink(e.target.value)}
              />
              <button
                className="bg-gray-200 grow px-4 rounded-full"
                onClick={addPhotosbyLink}
              >
                Add&nbsp;photo
              </button>
            </div>
            <div className="mt-2 grid gap-2 grid-cols-3 md:grid-cols-4 lg:grid-cols-6 mt-2">
              {addedPhotos.length > 0 &&
                addedPhotos.map((photo) => (
                  <div>
                    <img className="rounded-2xl"
                      src={"http://localhost:4000/uploads/" + photo}
                      alt={photo}
                    />
                  </div>
                ))}
              <label className=" cursor-pointer flex items-center gap-1 justify-center border bg-transparent rounded-2xl p-2 text-2xl text-gray-600">
                <input type="file" className="hidden" onChange={u}/>
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  fill="none"
                  viewBox="0 0 24 24"
                  strokeWidth={1.5}
                  stroke="currentColor"
                  className="w-8 h-8"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    d="M9 8.25H7.5a2.25 2.25 0 0 0-2.25 2.25v9a2.25 2.25 0 0 0 2.25 2.25h9a2.25 2.25 0 0 0 2.25-2.25v-9a2.25 2.25 0 0 0-2.25-2.25H15m0-3-3-3m0 0-3 3m3-3V15"
                  />
                </svg>
                upload
              </label>
            </div>
            {input("Description", "Enter the Description for your places")}
            <textarea
              value={description}
              onChange={(e) => setDescription(e.target.value)}
            />
            {input("Perks", "Select all the Perks")}
            <Perks selected={perks} onChange={setPerks} />
            {input("Extra Info", "Please provide the extra info")}
            <textarea
              value={extrainfo}
              onChange={(e) => setExtrainfo(e.target.value)}
            />
            {input(
              "Check-In & Check-Out & Max-Guest",
              "Add Check-In Check-Out and Max-Guest"
            )}
            <div className="grid sm:grid-cols-3 gap-2">
              <div>
                <h3 className="mt-2 -mb-1 ">Check in Time</h3>
                <input
                  type="text"
                  placeholder="16:00"
                  value={checkIn}
                  onChange={(e) => setCheckIn(e.target.value)}
                />
              </div>
              <div>
                <h3 className="mt-2 -mb-1 ">check out time</h3>
                <input
                  type="text"
                  placeholder="11"
                  value={checkOut}
                  onChange={(e) => setCheckOut(e.target.value)}
                />
              </div>
              <div>
                <h3 className="mt-2 -mb-1 "> Max Number of guest</h3>
                <input
                  type="number"
                  placeholder=""
                  value={maxGuest}
                  onChange={(e) => setMaxGuest(e.target.value)}
                />
              </div>
            </div>
            <button className="primary mt-4">Save</button>
          </form>
        </div>
      )}
    </div>
  );
}
